package main

import (
	"blockchain-crypto/vdf/pietrzak"
	"encoding/base64"
	"fmt"
)

func main() {
	out := pietrzak.Execute(170, 100)
	// 将vrf0字节数组转换为 Base64 编码的字符串
	vrf0byte := base64.StdEncoding.EncodeToString(out)
	fmt.Println("===============", vrf0byte)

	proof, err := base64.StdEncoding.DecodeString("")
	if err != nil {
		fmt.Println("error : ", err)
	}
	fmt.Println(proof)
	out2 := pietrzak.Verify(170, 100, "")
	fmt.Println(out2)
}
